{
  "sparkSql": "SELECT e.EmployeeID, e.Name, e.Age, Department.Dept, Department.Salary, RANK() OVER (PARTITION BY Department.Dept ORDER BY Department.Salary DESC) AS salaryRanking, DENSE_RANK() OVER (PARTITION BY Department.Dept ORDER BY e.Age ASC) AS ageRanking, ROW_NUMBER() OVER (ORDER BY Department.Salary DESC) departmentRowNum, SUM(Department.Salary) OVER (PARTITION BY Department.Dept) AS rollingTotalSalary, AVG(Department.Salary) AS averageSalary FROM Employees AS e JOIN Department ON e.EmployeeID = Department.EmployeeID WHERE e.Age > 25 AND Department.dept = 'IT' GROUP BY e.EmployeeID, e.Name, e.Age, Department.Dept, Department.Salary HAVING AVG(Department.Salary) > 30000 ORDER BY Department.Salary DESC LIMIT 10;"
}
